+	    decl = NULL_TREE;
 	}
       else if (scope == current)
         {
@@ -5479,7 +5480,7 @@ handle_class_head (aggr, scope, id)
 	cp_error ("no file-scope type named `%D'", id);

       /* Inject it at the current scope.  */
-      if (!decl)
+      if (! decl)
 	decl = TYPE_MAIN_DECL (xref_tag (aggr, id, 1));
     }
