+/// Returns alignment for a type, or None if alignment is undefined
+pub fn alignment<'a, 'tcx>(cx: &LateContext<'a, 'tcx>, ty: Ty<'tcx>) -> Option<Align> {
+    (cx.tcx, cx.param_env).layout_of(ty).ok().map(|layout| layout.align)
