+                             .last()
+                             .map(|tp| end_typaram(tp))
+                             .unwrap_or(span.lo);
+    let args_span = mk_sp(span_after(mk_sp(args_start, span.hi), "(", context.codemap),
+                          span_for_return(&fd.output).lo);
