+fn main() {
+    let x = vec![1];
+    let y = x;
+    //~^ HELP use a `ref` binding as shown
+    //~| SUGGESTION let ref y = x;
+    x; //~ ERROR use of moved value
+
+    let x = vec![1];
+    let mut y = x;
+    //~^ HELP use a `ref` binding as shown
+    //~| SUGGESTION let ref mut y = x;
+    x; //~ ERROR use of moved value
+
+    let x = (Some(vec![1]), ());
+
+    match x {
+        (Some(y), ()) => {},
+        //~^ HELP use a `ref` binding as shown
+        //~| SUGGESTION (Some(ref y), ()) => {},
+        _ => {},
+    }
+    x; //~ ERROR use of partially moved value
+}
