+fn rustc_sysroot() -> str {
+    alt os::get_exe_path() {
+        some(_path) {
+            let path = [_path, "..", "bin", "rustc"];
+            check vec::is_not_empty(path);
+            let rustc = fs::normalize(fs::connect_many(path));
+            #debug("  rustc: %s", rustc);
+            rustc
+        }
+        none { "rustc" }
+    }
