+}
+
+impl Drop for Foo {
+    fn drop(&mut self) {
+        println!("drop {}", self.x);
+    }
+}
+
+fn main() {
+    let mut ptr = ~Foo { x: 0 };
+    let test = |foo: &Foo| {
+        println!("access {}", foo.x);
+        ptr = ~Foo { x: ptr.x + 1 };
+        println!("access {}", foo.x);
+    };
+    test(ptr);
+    //~^ ERROR: cannot borrow `*ptr` as immutable
+}
