+fn change_vis(ctx: AssistCtx, vis: ast::Visibility) -> Option<Assist> {
+    if vis.syntax().text() != "pub" {
+        return None;
+    }
+    ctx.build("chage to pub(crate)", |edit| {
+        edit.replace(vis.syntax().range(), "pub(crate)");
+        edit.set_cursor(vis.syntax().range().start());
+    })
+}
