+
+// Test coercions between pointers which don't do anything fancy like unsizing.
+
+pub fn main() {
+    // &mut -> &
+    let x: &mut int = &mut 42i;
+    let x: &int = x;
+
+    let x: &int = &mut 42i;
+
+    // & -> *const
+    let x: &int = &42i;
+    let x: *const int = x;
+
+    let x: *const int = &42i;
+
+    // &mut -> *const
+    let x: &mut int = &mut 42i;
+    let x: *const int = x;
+
+    let x: *const int = &mut 42i;
+
+    // *mut -> *const
+    let x: *mut int = &mut 42i;
+    let x: *const int = x;
+}
