+fn is_boolean_literal(lit: Option<&tt::TokenTree>) -> bool {
+    if let Some(tt::TokenTree::Leaf(tt::Leaf::Literal(lit))) = lit {
+        if lit.text == "true" || lit.text == "false" {
+            return true;
+        }
+    }
+
+    false
+}
