+fn vis_offset(node: &SyntaxNode) -> TextUnit {
+    node.children()
+        .skip_while(|it| match it.kind() {
+            WHITESPACE | COMMENT | ATTR => true,
+            _ => false,
+        })
+        .next()
+        .map(|it| it.range().start())
+        .unwrap_or(node.range().start())
+}
