+fn fold_struct(cx: &Context, def: &ast::struct_def) -> @ast::struct_def {
+    let mut fields = def.fields.iter().map(|c| c.clone()).filter(|m| {
+        (cx.in_cfg)(m.node.attrs)
+    });
+    @ast::struct_def {
+        fields: fields.collect(),
+        ctor_id: def.ctor_id,
+    }
+}
