+struct Wrapper(~str);
+
+impl Wrapper {
+    pub fn new(wrapped: ~str) -> Wrapper {
+        Wrapper(wrapped)
+    }
+
+    pub fn say_hi(&self) {
+        println(fmt!("hello %s", **self));
+    }
+}
+
+impl Drop for Wrapper {
+    fn drop(&mut self) {}
+}
+
+pub fn main() {
+    {
+        // This runs without complaint.
+        let x = Wrapper::new(~"Bob");
+        x.say_hi();
+    }
