+fn check_doc_alias_attrs(
+    attrs: &[ast::Attribute],
+    item_kind: &str,
+    diagnostic: &::rustc_errors::Handler,
+) {
+    for attr in attrs {
+        if let Some(attr) = attr.meta() {
+            if let Some(list) = attr.meta_item_list() {
+                for meta in list {
+                    if meta.check_name(sym::alias) {
+                        diagnostic.span_err(
+                            meta.span(),
+                            &format!("`#[doc(alias = \"...\")]` isn't allowed on {}", item_kind),
+                        );
+                    }
+                }
+            }
+        }
+    }
+}
