+fn resdoc_from_resource(
+    name: str,
+    id: ast::node_id
+) -> doc::resdoc {
+    ~{
+        id: id,
+        name: name,
+        brief: none,
+        desc: none,
+        args: [],
+        sig: none
+    }
+}
+
+#[test]
+fn should_extract_resources() {
+    let source = "resource r(b: bool) { }";
+    let ast = parse::from_str(source);
+    let doc = extract(ast, "");
+    assert doc.topmod.resources[0].id != 0;
+    assert doc.topmod.resources[0].name == "r";
+}
