@@ -0,0 +1,21 @@
+// run-rustfix
+#![allow(unused)]
+#![warn(clippy::all)]
+#![feature(slice_patterns)]
+
+fn main() {
+    let v = Some(true);
+    let s = [0, 1, 2, 3, 4];
+    match v {
+        Some(x) => (),
+        y => (),
+    }
+    match v {
+        Some(x) => (),
+        y @ None => (), // no error
+    }
+    match s {
+        [x, inside @ .., y] => (), // no error
+        [..] => (),
+    }
+}

