+#[cfg(test)]
+mod tests {
+    use comm::*;
+    use prelude::*;
+
+    #[test]
+    fn test_nested_recv_iter() {
+        let (port, chan) = stream::<int>();
+        let (total_port, total_chan) = oneshot::<int>();
+
+        do spawn {
+            let mut acc = 0;
+            for x in port.recv_iter() {
+                acc += x;
+                for x in port.recv_iter() {
+                    acc += x;
+                    for x in port.try_recv().move_iter() {
+                        acc += x;
+                        total_chan.send(acc);
+                    }
+                }
+            }
+        }
+
+        chan.send(3);
+        chan.send(1);
+        chan.send(2);
+        assert_eq!(total_port.recv(), 6);
+    }
+
+    #[test]
+    fn test_recv_iter_break() {
+        let (port, chan) = stream::<int>();
+        let (count_port, count_chan) = oneshot::<int>();
+
+        do spawn {
+            let mut count = 0;
+            for x in port.recv_iter() {
+                if count >= 3 {
+                    count_chan.send(count);
+                    break;
+                } else {
+                    count += x;
+                }
+            }
+        }
+
+        chan.send(2);
+        chan.send(2);
+        chan.send(2);
+        chan.send(2);
+        assert_eq!(count_port.recv(), 4);
+    }
+}
