+use std::comm;
+use std::io::timer::Timer;
+
+pub fn main() {
+    let (port, chan) = Chan::new();
+    spawn(proc (){
+        let mut timer = Timer::new().unwrap();
+        timer.sleep(10);
+        chan.send(());
+    });
+    loop {
+        match port.try_recv() {
+            comm::Data(()) => break,
+            comm::Empty => {}
+            comm::Disconnected => unreachable!()
+        }
+    }
+}
