+
+union U {
+    x: u32,
+    y: f32,
+}
+
+struct V {
+    x: u32,
+    y: u32,
+}
+
+fn main() {
+    let u = U { x: 0x3f800000 };
+    let _f = unsafe { u.y };
+    let v = V { x: 0, y: 0 };
+    println!("{}", v.x);
+}
