+
+// Test DST raw pointers
+
+trait Trait {
+    fn foo(&self) -> int;
+}
+
+struct A {
+    f: int
+}
+impl Trait for A {
+    fn foo(&self) -> int {
+        self.f
+    }
+}
+
+pub struct Foo<Sized? T> {
+    f: T
+}
+
+pub fn main() {
+    // raw trait object
+    let x = A { f: 42 };
+    let y: *const A = &x;
+    let z: *const Trait = y;
+    let r = unsafe {
+        (&*z).foo()
+    };
+    assert!(r == 42);
+
+    // raw DST struct
+    let p = Foo {f: A { f: 42 }};
+    let q: *const Foo<A> = &p;
+    let o: *const Foo<Trait> = q;
+    let r = unsafe {
+        (&*o).f.foo()
+    };
+    assert!(r == 42);
+
+    // raw slice
+    let a: *const [_] = &[1i, 2, 3];
+    unsafe {
+        let b = (*a)[2];
+        assert!(b == 3);
+    }
+
+    // raw DST struct with slice
+    let c: *const Foo<[_]> = &Foo {f: [1i, 2, 3]};
+    unsafe {
+        let b = (&*c).f[0];
+        assert!(b == 1);
+    }
+
+    // all of the above with *mut
+    let mut x = A { f: 42 };
+    let y: *mut A = &mut x;
+    let z: *mut Trait = y;
+    let r = unsafe {
+        (&*z).foo()
+    };
+    assert!(r == 42);
+
+    let mut p = Foo {f: A { f: 42 }};
+    let q: *mut Foo<A> = &mut p;
+    let o: *mut Foo<Trait> = q;
+    let r = unsafe {
+        (&*o).f.foo()
+    };
+    assert!(r == 42);
+
+    let a: *mut [_] = &mut [1i, 2, 3];
+    unsafe {
+        let b = (*a)[2];
+        assert!(b == 3);
+    }
+
+    let c: *mut Foo<[_]> = &mut Foo {f: [1i, 2, 3]};
+    unsafe {
+        let b = (&*c).f[0];
+        assert!(b == 1);
+    }
+}
