+  //~| SUGGESTION let y: &(&_, u8) = &(&1, 2);

   let ref z = 1 + 2;
   //~^ ERROR `ref` on an entire `let` pattern is discouraged
   //~| HELP try
   //~| SUGGESTION let z = &(1 + 2);

+  let ref mut z = 1 + 2;
+  //~^ ERROR `ref` on an entire `let` pattern is discouraged
+  //~| HELP try
+  //~| SUGGESTION let mut z = &(1 + 2);
