+// xfail-fast
+// exec-env:RUST_LOG=debug
+
+#[feature(managed_boxes)];
+
+use std::fmt;
+
+struct Foo(@mut int);
+
+impl fmt::Default for Foo {
+    fn fmt(f: &Foo, _fmt: &mut fmt::Formatter) {
+        assert!(***f == 0);
+        ***f = 1;
+    }
+}
+
+pub fn main() {
+    let (p,c) = stream();
+    do spawn {
+        let f = Foo(@mut 0);
+        debug!("{}", f);
+        assert!(**f == 1);
+        c.send(());
+    }
+    p.recv();
+}
