+        UvEventLoop {
+            uvio: 0
+        }
+    }
+}
+
+impl EventLoop for UvEventLoop {
+}
+
+pub struct Scheduler {
+    event_loop: ~EventLoop,
+}
+
+impl Scheduler {
+
+    pub fn new(event_loop: ~EventLoop) -> Scheduler {
+        Scheduler {
+            event_loop: event_loop,
+        }
+    }
+}
+
+fn main() {
+    let mut sched = Scheduler::new(~UvEventLoop::new() as ~EventLoop);
