+struct X(int);
+
+enum Enum {
+    Variant1,
+    Variant2
+}
+
+impl Drop for X {
+    fn drop(&mut self) {}
+}
+impl Drop for Enum {
+    fn drop(&mut self) {}
+}
+
+fn main() {
+    let foo = X(1i);
+    drop(foo);
+    match foo { //~ ERROR use of moved value
+        X(1i) => (),
+        _ => unreachable!()
+    }
+
+    let e = Variant2;
+    drop(e);
+    match e { //~ ERROR use of moved value
+        Variant1 => unreachable!(),
+        Variant2 => ()
+    }
+}
