+
+extern fn voidret1() {}
+extern fn voidret2() {}
+
+extern fn uintret() -> uint { 22 }
+
+extern fn uintvoidret(x: uint) {}
+
+extern fn uintuintuintuintret(x: uint, y: uint, z: uint) -> uint { x+y+z }
+
+fn main() {
+    assert_eq!(voidret1, voidret1);
+    assert!(voidret1 != voidret2);
+
+    assert_eq!(uintret, uintret);
+
