+use std::ops;
+
+#[simd] struct f32x4(f32, f32, f32, f32);
+
+fn add<T: ops::Add<T, T>>(lhs: T, rhs: T) -> T {
+    lhs + rhs
+}
+
+impl ops::Add<f32x4, f32x4> for f32x4 {
+    fn add(&self, rhs: &f32x4) -> f32x4 {
+        *self + *rhs
+    }
+}
+
+fn main() {
+    let lr = f32x4(1.0f32, 2.0f32, 3.0f32, 4.0f32);
+
+    // lame-o
+    let f32x4(x, y, z, w) = add(lr, lr);
+    assert_eq!(x, 2.0f32);
+    assert_eq!(y, 4.0f32);
+    assert_eq!(z, 6.0f32);
+    assert_eq!(w, 8.0f32);
+}
