+fn is_static<T: 'static>() {}
+
+pub fn main() {
+    foo::<proc()>();
+    foo::<proc:()>();
+    foo::<proc:Send()>();
+    foo::<proc:Send + Freeze()>();
+    foo::<proc:'static + Send + Freeze()>();
+
+    is_send::<proc:Send()>();
+    is_freeze::<proc:Freeze()>();
+    is_static::<proc:'static()>();
+
+
+    let a = 3;
+    bar::<proc:()>(proc() {
+        let b = &a;
+        println!("{}", *b);
+    });
