+use std::num::Zero;
+
+pub struct X<T> {
+  a: T
+}
+
+// reordering these bounds stops the ICE
+impl<T: Zero + Eq + Zero>
+  Zero for X<T> {
+    fn zero() -> X<T> {
+      X { a: Zero::zero() }
+    }
+    fn is_zero(&self) -> bool {
+        self.a.is_zero()
+    }
+}
+
+macro_rules! constants {
+  () => {
+    let _0 : X<int> = Zero::zero();
+   }
+}
+
+
+pub fn main() {
+  constants!();
+}
