+fn f(x: int) -> int { x }
+fn g(x: int) -> int { 2 * x }
+
+static F: extern fn(int) -> int = f;
+static mut G: extern fn(int) -> int = f;
+
+pub fn main() {
+    assert_eq!(F(42), 42);
+    unsafe {
+        assert_eq!(G(42), 42);
+        G = g;
+        assert_eq!(G(42), 84);
+    }
+}
