-pub struct UdpWatcher {
-    handle: *mut uvll::uv_udp_t,
-    home: HomeHandle,
-
-    // See above for what these fields are
-    refcount: Refcount,
-    read_access: AccessTimeout<()>,
-    write_access: AccessTimeout<()>,
-
-    blocked_sender: Option<BlockedTask>,
-}
-
-struct UdpRecvCtx {
-    task: Option<BlockedTask>,
-    buf: Option<Buf>,
-    result: Option<(ssize_t, Option<rtio::SocketAddr>)>,
-}
-
-struct UdpSendCtx {
-    result: c_int,
-    data: Option<Vec<u8>>,
-    udp: *mut UdpWatcher,
-}
-
-impl UdpWatcher {
-    pub fn bind(io: &mut UvIoFactory, address: rtio::SocketAddr)
-                -> Result<UdpWatcher, UvError> {
-        let udp = UdpWatcher {
-            handle: unsafe { uvll::malloc_handle(uvll::UV_UDP) },
-            home: io.make_handle(),
-            refcount: Refcount::new(),
-            read_access: AccessTimeout::new(()),
-            write_access: AccessTimeout::new(()),
-            blocked_sender: None,
-        };
-        assert_eq!(unsafe {
-            uvll::uv_udp_init(io.uv_loop(), udp.handle)
-        }, 0);
-        let mut storage = unsafe { mem::zeroed() };
-        let _len = addr_to_sockaddr(address, &mut storage);
-        let result = unsafe {
-            let addr_p = &storage as *const _ as *const libc::sockaddr;
-            uvll::uv_udp_bind(udp.handle, addr_p, 0u32)
-        };
-        return match result {
-            0 => Ok(udp),
-            n => Err(UvError(n)),
-        };
-    }
-}
