-    fn clone(&self) -> Box<rtio::RtioUdpSocket + Send> {
-        box UdpWatcher {
-            handle: self.handle,
-            home: self.home.clone(),
-            refcount: self.refcount.clone(),
-            write_access: self.write_access.clone(),
-            read_access: self.read_access.clone(),
