+fn simd_ops_f64() {
+    let a = f64x4::splat(10.0);
+    let b = f64x4::from_array([1.0, 2.0, 3.0, -4.0]);
+    assert_eq!(-b, f64x4::from_array([-1.0, -2.0, -3.0, 4.0]));
