+trait Trait {}
+
+#[deriving(Show)]
+struct Foo<T: Trait> {
+    foo: T,
+}
+
+#[deriving(Show)]
+struct Bar<T> where T: Trait {
+    bar: T,
+}
+
+impl Trait for int {}
+
+fn main() {
+    let a = Foo { foo: 12i };
+    let b = Bar { bar: 12i };
+    println!("{} {}", a, b);
+}
