+#![deny(warnings)]
+#![allow(unused_imports)]
+
+enum Foo { A }
+mod bar {
+    pub fn normal(x: ::Foo) {
+        use A;
+        match x {
+            A => {}
+        }
+    }
+    pub fn wrong(x: ::Foo) {
+        match x {
+            ::A => {}
+        }
+    }
+}
+
+pub fn main() {
+    bar::normal(A);
+    bar::wrong(A);
+}
