+use std::os;
+use std::io::{stdio, Command};
+
+fn main() {
+    let args = os::args();
+    if args.len() > 1 {
+        let mut out = stdio::stdout();
+        out.write(['a' as u8, ..128 * 1024]).unwrap();
+    } else {
+        let out = Command::new(args.get(0).as_slice()).arg("child").output();
+        let out = out.unwrap();
+        assert!(out.status.success());
+    }
+}
