+        commonjs({
+            namedExports: {
+                // squelch missing import warnings
+                'vscode-languageclient': ['CreateFile', 'RenameFile']
+            }
+        })
+    ],
+    external: [...nodeBuiltins, 'vscode'],
+    output: {
+        file: './out/extension.js',
+        format: 'cjs'
+    }
+};

