+// aux-build:fat_drop.rs
+
+#![feature(core_intrinsics)]
+
+extern crate fat_drop;
+
+fn main() {
+    unsafe {
+        let s: &mut fat_drop::S = std::mem::uninitialized();
+        std::intrinsics::drop_in_place(s);
+        assert!(fat_drop::DROPPED);
+    }
+}
