+fn check_nan(cx: &LateContext<'_, '_>, expr: &Expr, cmp_span: Span) {
+    if let Some((value, _)) = constant(cx, cx.tables, expr) {
+        let needs_lint = match value {
+            Constant::F32(num) => num.is_nan(),
+            Constant::F64(num) => num.is_nan(),
+            _ => false,
+        };
+
+        if needs_lint {
+            span_lint(
