+
+pub static global: int = 3;
+
+pub fn verify_same(a: &'static int) {
+    let a = a as *int as uint;
+    let b = &global as *int as uint;
+    assert_eq!(a, b);
+}
+
+condition!{ pub test: int -> (); }
+
+pub fn raise() {
+    test::cond.raise(3);
+}

