+    assert(cttz16(100i16) == 2i16);
+    assert(cttz32(100i32) == 2i32);
+    assert(cttz64(100i64) == 2i64);
+
+    assert(cttz8(-1i8) == 0i8);
+    assert(cttz16(-1i16) == 0i16);
+    assert(cttz32(-1i32) == 0i32);
