+impure fn parse_full_import_name(parser p) {
+    alt (p.peek()) {
+        case (token.IDENT(?ident)) {
+            p.bump();
+            parse_rest_import_name(p, ident);
+        }
+        case (_) {
+            p.err("expecting an identifier");
+        }
+    }
+}
+
+impure fn parse_import(parser p) {
+    alt (p.peek()) {
+        case (token.IDENT(?ident)) {
+            p.bump();
+            alt (p.peek()) {
+                case (token.EQ) {
+                    p.bump();
+                    parse_full_import_name(p);
+                }
+                case (_) {
+                    parse_rest_import_name(p, ident);
+                }
+            }
+        }
+        case (_) {
+            p.err("expecting an identifier");
+        }
+    }
+}
