pub trait Reborrow {
    type Output<'reborrow>
    where
        Self: 'reborrow;

    fn reborrow(&mut self) -> Self::Output<'_>;
}

impl<'a, T: ?Sized> Reborrow for Option<&'a mut T> {
    type Output<'reborrow> = Option<&'reborrow mut T> where Self:'reborrow;

    fn reborrow(&mut self) -> Self::Output<'_> {
        match self {
            None => None,
            Some(v) => Some(&mut *v),
        }
    }
}

struct GRef<'g>(&'g mut ());

impl<'g> Reborrow for GRef<'g> {
    type Output<'x> = GRef<'x> where Self: 'x;
    fn reborrow(&mut self) -> GRef<'_> {
        GRef(self.0)
    }
}

struct S;
fn foo(mut g: GRef<'_>, mut v: Option<&mut S>, f: fn(GRef<'_>, Option<&mut S>)) {
    for _ in 0..10 {
        (f)(g.reborrow(), v.reborrow())
    }
}

