use std::num::sqrt;

struct Vec2 {
    x: f32,
    y: f32,
}

impl Vec2 {
    fn new(x: f32, y: f32) -> Vec2 {
        Vec2 {x: x, y: y}
    }

    fn normalized(&self) -> Vec2 {
        self / self.length()
    }

    fn length(&self) -> f32 {
        sqrt(self.length_squared())
    }

    fn length_squared(&self) -> f32 {
        self.x * self.x +
        self.y * self.y
    }
}

impl Div<f32, Vec2> for Vec2 {
    fn div(&self, scalar: f32) -> Vec2 {
        let inverse = 1.0f32 / scalar;
        Vec2 {
            x: self.x * inverse,
            y: self.y * inverse,
        }
    }
}

#[cfg(test)]
mod tests {
    use super::Vec2;

    #[test]
    fn test_length() {
        let v = Vec2::new(3, 4);
        let len = v.length();
        assert!(len > 0.49 && len < 0.51);
    }
}
