trait Node {
    fn zomg();
}

trait Graph<N: Node> {
    fn nodes<'a, I: Iterator<&'a N>>(&'a self) -> I;
}

impl<N: Node> Graph<N> for Vec<N> {
    fn nodes<'a, I: Iterator<&'a N>>(&self) -> I {
        self.iter()
    }
}

struct Stuff;

impl Node for Stuff {
    fn zomg() {
        println!("zomg");
    }
}

fn iterate<N: Node, G: Graph<N>>(graph: &G) {
    for node in graph.iter() {
        node.zomg();
    }
}

pub fn main() {
    let graph = Vec::new();

    graph.push(Stuff);

    iterate(graph);
}
