struct MyVec<T> {
    data: Vec<T>,
}

impl<T> Index<uint, T> for MyVec<T> {
    fn index<'a>(&'a self, &i: &uint) -> &'a T {
        assert!(i < self.data.len());
        &self.data.as_slice()[i]
    }
}

fn main() {
    let v = MyVec { data: vec!(box 1i, box 2, box 3) };

    let ok: &Box<int> = v.index(&0);

    // Partial move while `v` is borrowed
    let bad: Box<int> = v[0];

    // Boom!
    println!("{}", good);
}
