extern crate libc;

use std::num::{Bounded, ToPrimitive};

fn char_signed() -> bool {
    (-1 as libc::c_char) < 0
}

fn get_type<T: Bounded+ToPrimitive>() -> &'static str {
    // XXX: this will break if any of these types is ever larger than u64
    static UCHAR_MAX:     u64 = -1 as libc::c_uchar as u64;
    static SCHAR_MAX:     u64 = UCHAR_MAX / 2;
    static USHORT_MAX:    u64 = -1 as libc::c_ushort as u64;
    static SHORT_MAX:     u64 = USHORT_MAX / 2;
    static UINT_MAX:      u64 = -1 as libc::c_uint as u64;
    static INT_MAX:       u64 = UINT_MAX / 2;
    static ULONG_MAX:     u64 = -1 as libc::c_ulong as u64;
    static LONG_MAX:      u64 = ULONG_MAX / 2;
    static ULONGLONG_MAX: u64 = -1 as libc::c_ulonglong as u64;
    static LONGLONG_MAX:  u64 = ULONGLONG_MAX / 2;

    let max: T = Bounded::max_value();
    match max.to_u64().unwrap() {
        UCHAR_MAX     if char_signed() => "unsigned char",
        UCHAR_MAX     => "char",
        SCHAR_MAX     if char_signed() => "char",
        SCHAR_MAX     => "signed char",
        USHORT_MAX    => "unsigned short",
        SHORT_MAX     => "short",
        UINT_MAX      => "unsigned int",
        INT_MAX       => "int",
        ULONG_MAX     => "unsigned long",
        LONG_MAX      => "long",
        ULONGLONG_MAX => "unsigned long long",
        LONGLONG_MAX  => "long long",
        _ => fail!(),
    }
}

fn main() {
    println!("{}", get_type::<u16>());
}
