struct Node{  left:Option<Box<Node>>  }

fn leftmost_child(n:& mut Node)-> & mut Node {
    let mut leftest = n;
    loop {
        match leftest.left.as_mut() {
            Some(nl) => leftest = &mut **nl,
            None => break,
        }
    }
    leftest
}

