// lib.rs
#![feature(associated_types)]

use std::num::Zero;

pub trait AdditiveIterator {
    type Output;

    fn sum(&mut self) -> <Self as AdditiveIterator>::Output;
}

impl<T, I> AdditiveIterator for I where
    T: Add<T, T> + Zero,
    I: Iterator<T>,
{
    type Output = T;

    fn sum(&mut self) -> T {
        self.fold(Zero::zero(), |s, x| x + s)
    }
}
