#![feature(core,)]
use std::mem;
use std::ops::{
    Deref,
};

pub trait Array {
    type Item;
    fn base_ptr(&self) -> *const Self::Item;
}

pub struct FixVec<V> {
    len: i8,
    xs: V,
}

impl<V: Array> Deref for FixVec<V>
{
    type Target = [V::Item];

    #[inline]
    fn deref(&self) -> &[V::Item]
    {
        unsafe {
            mem::transmute(std::raw::Slice {
                data: self.xs.base_ptr(),
                len: self.len as usize,
            })
        }
    }
}

