}

unsafe impl MySend for .. { }

unsafe impl<T: MySend + ?Sized> MySend for Unique<T> { }

pub struct Unique<T:?Sized> {
    pointer: *const T,
}

pub struct Node<V> {
    vals: V,
    edges: Unique<Node<V>>,
}

fn is_send<T: MySend>() {}

fn main() {
    is_send::<Node<&'static ()>>();
