#![feature(rc_weak)] 

use std::rc::{ Rc, Weak };
use std::cell::RefCell;

trait Barker {
  fn bark(&self);
}

struct Dog {
  x: i32,
  y: i32,
}

impl Barker for Dog {
  fn bark(&self) {
    println!("bark")
  }
}

fn speak_weak(barker: Weak<RefCell<Barker>>) {
  let b = barker.upgrade().unwrap();
  let c = b.borrow();
  c.bark();
}

fn speak_rc(barker: Rc<RefCell<Barker>>) {
  let b = barker.clone();
  let c = b.borrow();
  c.bark();
}

fn main() {
  let dog = Rc::new(RefCell::new(Dog { x: 0, y: 0 }));

  // works
  speak_rc(dog.clone() as Rc<RefCell<Barker>>);

  // doesn't work
  speak_weak(dog.downgrade() as Weak<RefCell<Barker>>);
}
```

